mapper.update:
UPDATE t_hr_Staffturnover
FID = #{id,jdbcType=VARCHAR}, FNAME = #{name,jdbcType=VARCHAR}, FNUMBER = #{number,jdbcType=VARCHAR}, FSIMPLEPINYIN = #{simplePinyin,jdbcType=VARCHAR}, FDESCRIPTION = #{description,jdbcType=VARCHAR}, FCREATETIME = #{createTime,jdbcType=TIMESTAMP}, FKCREATORID = #{creator.id,jdbcType=VARCHAR}, FLASTUPDATETIME = #{lastUpdateTime,jdbcType=TIMESTAMP}, FKUPDATORID = #{updator.id,jdbcType=VARCHAR}, FKCONTROLUNITID = #{controlUnit.id,jdbcType=VARCHAR}, FKORGID = #{org.id,jdbcType=VARCHAR}, FSUBMITDATE = #{submitDate,jdbcType=TIMESTAMP}, FAUDITDATE = #{auditDate,jdbcType=TIMESTAMP}, FPROCESSINSTANCE = #{processInstance,jdbcType=VARCHAR}, FKAPPLYPERSONID = #{applyPerson.id,jdbcType=VARCHAR}, FAPPLYPERSONNAME = #{applyPersonName,jdbcType=VARCHAR}, FKAPPLYORGID = #{applyOrg.id,jdbcType=VARCHAR}, FKAPPLYCHANGEORGID = #{applyChangeOrg.id,jdbcType=VARCHAR}, FKAPPLYPOSITIONID = #{applyPosition.id,jdbcType=VARCHAR}, FKAPPLYCHANGEPOSITIONID = #{applyChangePosition.id,jdbcType=VARCHAR}, FKAPPLYJOBLEVELID = #{applyJoblevel.id,jdbcType=VARCHAR}, FKAPPLYCHANGEJOBLEVELID = #{applyChangeJoblevel.id,jdbcType=VARCHAR}, FBILLSTATUS = #{billStatus,jdbcType=VARCHAR}, FCHANGETYPE = #{changeType,jdbcType=VARCHAR}, FREMARK = #{remark,jdbcType=VARCHAR}, FVALIDATETIME = #{validateTime,jdbcType=TIMESTAMP}, FGIVETIME = #{giveTime,jdbcType=TIMESTAMP}, FKGIVEPERSONID = #{givePerson.id,jdbcType=VARCHAR}, FKBASICDATAID = #{basicData.id,jdbcType=VARCHAR}, FTITLE = #{title,jdbcType=VARCHAR}, FISEFFECTIVE = #{isEffective,jdbcType=INTEGER}
WHERE FID = #{id}

mapper.select:
SELECT D.FID AS "id", D.FNAME AS "name", D.FNUMBER AS "number", D.FSIMPLEPINYIN AS "simplePinyin", D.FDESCRIPTION AS "description", D.FCREATETIME AS "createTime", D.FKCREATORID AS "creator.id", D.FLASTUPDATETIME AS "lastUpdateTime", D.FKUPDATORID AS "updator.id", D.FKCONTROLUNITID AS "controlUnit.id", D.FKORGID AS "org.id", D.FSUBMITDATE AS "submitDate", D.FAUDITDATE AS "auditDate", D.FPROCESSINSTANCE AS "processInstance", D.FKAPPLYPERSONID AS "applyPerson.id", PE.FNAME AS "applyPersonName", D.FKAPPLYORGID AS "applyOrg.id", D.FKAPPLYCHANGEORGID AS "applyChangeOrg.id", D.FKAPPLYPOSITIONID AS "applyPosition.id", D.FKAPPLYCHANGEPOSITIONID AS "applyChangePosition.id", D.FKAPPLYJOBLEVELID AS "applyJoblevel.id", D.FKAPPLYCHANGEJOBLEVELID AS "applyChangeJoblevel.id", D.FBILLSTATUS AS "billStatus", D.FCHANGETYPE AS "changeType", D.FREMARK AS "remark", D.FVALIDATETIME AS "validateTime", D.FGIVETIME AS "giveTime", D.FKGIVEPERSONID AS "givePerson.id", D.FKBASICDATAID AS "basicData.id", p.fname as "applyPosition.name", D.FTITLE AS "title", PE.fjobStatus as "applyPerson.jobStatus", PE.fNUMBER AS "applyPerson.number", o.fname as "applyOrg.name", j.fname as "applyJoblevel.name", B.FNAME AS "basicData.name", D.FISEFFECTIVE AS "isEffective" FROM t_hr_Staffturnover D left join T_BD_POSITION P ON D.FKapplyPositionID = P.FID LEFT JOIN T_BD_ORG O ON D.FKapplyOrgID = O.FID LEFT JOIN T_BD_JOBLEVEL J ON J.FID = D.FKapplyJoblevelID left join t_bd_person PE on D.FKapplyPersonID = PE.FID LEFT JOIN T_BD_BASICDATA B ON D.FKBASICDATAID = B.FID
fchangeType = 'LEAVE' AND (PE.FNAME LIKE '%${key}%' OR D.FNUMBER LIKE '%${key}%' OR PE.FNUMBER LIKE '%${key}%' OR J.FNAME LIKE '%${key}%' OR P.FNAME LIKE '%${key}%') AND D.FKAPPLYORGID= #{orgId}
AND D.FID != #{notBillId,jdbcType=VARCHAR}
AND (D.FCREATETIME >= #{beginDate} and D.FCREATETIME <= addDate(#{endDate},1) )
AND D.FCREATETIME >= #{beginDate}
AND D.FCREATETIME <= addDate(#{endDate},1)
AND (D.FvalidateTime >= #{beginDate} and D.FvalidateTime <= #{endDate})
AND D.FvalidateTime >= #{beginDate}
AND D.FvalidateTime <= #{endDate}
AND O.FLONGNUMBER LIKE '${orgLongNumber}%'
AND D.FKAPPLYPERSONID = #{personId}
AND D.FBILLSTATUS = #{billSta}
AND D.FvalidateTime >now() AND D.FBILLSTATUS ='APPROVED'
AND D.FISEFFECTIVE = #{isEffective} AND getFormatDate(D.FvalidateTime) <= #{innerDate} AND D.FKCONTROLUNITID = #{controlUnitId}
order by D.FCREATETIME desc
